package com.config;import org.aeonbits.owner.Config;// ./gradlew clean test -Dbrowser=firefox@Config.LoadPolicy(Config.LoadType.MERGE)      //   переопределяются из какого окружения будет запускаться@Config.Sources({"system:properties", "classpath:config.properties", "classpath:allure.properties"}) // откуда будет читаться properties вфнные// interface Configuration extends Configpublic interface Configuration extends Config {    @Config.Key("allure.results.directory")    String allureResultsDir();        @Config.Key("base.url")    String baseUrl();        @Config.Key("base.test.video.path")    String baseTestVideoPath();        String browser();        boolean headless();        @Config.Key("slow.motion")    int slowMotion();        int timeout();        boolean video();}